// 檔案上傳
// $file 為 $_FILES
// $table 為 資料表
// $id 為 要上傳檔案的資料id
function Upload($file, $table, $id){
    // 以現在的時間戳記當檔名
    global $now;
    foreach($_FILES as $name => $file){
        // 抓取副檔名
        $ext = pathinfo($file["name"], PATHINFO_EXTENSION);
        // 和現在時間組合成完整檔名
        $fn = $now.".".$ext;
        // 移動到img資料夾
        copy($file["tmp_name"], "img/".$fn);
        // 資料庫更新
        All("update ".$table." set ". $name." = '".$fn."' where id = '".$id."'");
    }
}

